(function(e){e.fn.badger=function(e,t){var n=this.find("#Badger").html();if(!e){if(n){this.find("#Badger").remove()}}else{var r=this.find("#Badge").text();if(e.charAt(0)=="+"){if(isNaN(e.substr(1))){e=r+e.substr(1)}else{e=Math.round(Number(r)+Number(e.substr(1)))}}else if(e.charAt(0)=="-"){if(isNaN(e.substr(1))){e=r-e.substr(1)}else{e=Math.round(Number(r)-Number(e.substr(1)))}}if(n){this.find("#Badge").html(e)}else{this.append('<div class="badger-outter" id="Badger"><div class="badger-inner"><p class="badger-badge" id="Badge">'+e+"</p></div></div>")}if(isNaN(e)){this.find("#Badge").removeClass("badger-number").addClass("badger-text")}else{this.find("#Badge").removeClass("badger-text").addClass("badger-number")}if(t){t(e)}}}})(jQuery)